package lt.bt.Testas;

public class iki100while {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			while100();
		}
	
		public static void while100() {
			int i = 1;
	        while (i <= 100) {
	            System.out.print(i + " ");
	            i++;
	        }
		}
}

